mapper.select:
select distinct c.customer_id as customerId, c.customer_no as customerNo, c.customer_name as customerName, cb.customer_business_id as customerBusinessId, cb.business_type as businessType, decode(cb.business_type, 1, '专线', 2, '宽带', 3, '固话', 4, '云', 5, '移网', 6, '电路') as businessTypeName, cb.business_phone as businessPhone, cb.month_fee as monthFee from BD_BUILDING_CUSTOMER_RP t, BD_BUILDING b, BD_CUSTOMER c, bd_customer_business cb, addr_segm a where t.delete_flag = 0 and c.delete_flag = 0 and c.delete_flag = 0 and a.delete_state = '0' and cb.delete_flag = 0 and t.building_no = b.building_no and t.customer_no = c.customer_no and cb.customer_id = c.customer_id and t.segm_id = a.segm_id
AND t.building_no = #{buildingNo}
order by c.customer_id desc
