mapper.select:
select u.user_id userId, u.username, u.email, u.mobile, u.status, u.crate_time crateTime, u.ssex, d.dept_name deptName, u.staff_id staffId, s.name staffName, s.region_id regionId, r.region_name regionName, u.staff_type staffType, decode(u.staff_type, 0, '超级管理员', 1, '区域管理员', 2, '普通人员') staffTypeName from t_user u, t_dept d, staff s, spc_region r where 1 = 1 and u.dept_id = d.dept_id(+) and u.staff_id = s.staff_id(+) and s.region_id = r.region_id(+)
AND lower(u.username) = #{username}
AND d.dept_id = #{deptId}
AND u.ssex = #{ssex}
AND u.status = #{status}
AND s.name like #{bindStaffName}
order by u.user_id desc
